import React, { useEffect, useState } from "react";
import styles from "./AvailableMeals.module.css";
import MealItem from "./MealItem";

const AvailableMeals = (props) => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();

  useEffect(() => {
    const fetchMeals = async () => {
      // const response = await fetch(
      // "https://react-food-645a1-default-rtdb.europe-west1.firebasedatabase.app/meals.json"
      // );

      // if (!response.ok) {
      //  throw new Error("Somthing went wrong!");
      //  }

      //const responseData = await response.json();

      const loadedMeals = [
        { id: 1, name: "sushi", description: "bla", price: 39, sale: 0.2 },

        { id: 2, name: "sushi", description: "bla", price: 40 },
        { id: 3, name: "sushi", description: "bla", price: 52 },
      ];

      //  for (const key in responseData) {
      //  loadedMeals.push({
      //    id: key,
      //    name: responseData[key].name,
      //    description: responseData[key].description,
      //     price: responseData[key].price,
      //   });
      //  }
      setMeals(loadedMeals);
      setIsLoading(false);
    };

    fetchMeals().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={styles.MealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section className={styles.MealsError}>
        <p>{httpError}</p>
      </section>
    );
  }

  const mealsList = meals.map((meal) => {
    if (meal.sale) {
      return (
        <MealItem
          id={meal.id}
          key={meal.id}
          name={meal.name}
          description={meal.description}
          price={meal.price}
          sale={meal.sale}
        />
      );
    } else {
      return (
        <MealItem
          id={meal.id}
          key={meal.id}
          name={meal.name}
          description={meal.description}
          price={meal.price}
        />
      );
    }
  });

  return (
    <section className={styles.meals}>
      <ul className={styles.card_layout}>{mealsList}</ul>
    </section>
  );
};

export default AvailableMeals;
