import { Fragment,useState } from "react/cjs/react.production.min";
import Header from "./components/Layout/Header";
import MealsSummary from "./components/Meals/MealsSummary";
import Card from "./components/UI/Card";
import AvailableMeals from "./components/Meals/AvailableMeals";


function App(props) {

const [cartNum,setCartNum]= useState(0);

const meals=[{
  meal:'sushi',
  description:'fish and veg',
  price:'33',
  key:1 
},
{
  meal:'pizza',
  description:'pizza la italia',
  price:'20',
  key:2 
},
{
  meal:'hamburger',
  description:'classic burger with fries',
  price:'40',
  key:3
}
];


const onAddHandler=(order)=>{
setCartNum(cartNum+order)
};

  return (
    <Fragment>
      <Header ></Header>
      <MealsSummary></MealsSummary>
      <Card>
        <AvailableMeals meals={meals} onAdd={onAddHandler}/>
      </Card>
      </Fragment>
  );
}

export default App;
